%% Transcriptomic analyses
%%% BrainSpace toolbox is necessary: https://github.com/MICA-MNI/BrainSpace 
%%% For more information about BrainSpace, please see our tutorial:
%%% https://brainspace.readthedocs.io/en/latest/index.html
%%% parcellation: Schaefer-200; number of nodes: 200
%%% NOTE: self-defined parameters in line 10-25, line 48, line 230


clear;clc; close all
parcell_Num = 200;
ParcelName  = 'Schaefer_200';
NumVert     = 32492; % for spin test
NumPerm     = 1000; % 1000 times sipn tests

% load surfaces
[surf_lh, surf_rh]     = load_conte69();
[sphere_lh, sphere_rh] = load_conte69('spheres');
if strcmp(ParcelName, 'Schaefer_200') == 1
    NumROI             = 200;
    labeling           = load_parcellation('schaefer',NumROI);
    exp_tmp            = ['parc = labeling.schaefer_',int2str(NumROI),';'];
end
eval(exp_tmp);

Path               = strcat('yourpath/CDC/');

%%%%% Step #1: Generate gene list & filtering
% load genes
[~,tot_gene_list,~]    = xlsread(strcat(Path,'\data\',ParcelName, '/tot_gene_list.xlsx'));
load(strcat(Path,'/data/',ParcelName, '/tot_gene_consis_lh.mat'));
load(strcat(Path,'/data/',ParcelName, '/tot_gene_consis_bh.mat'));

% filtering (inter-donal consistency r>0.5)
cons_idx               = find(tot_gene_consis_lh(:,1)>0.5);
cons_gene_list         = tot_gene_list(cons_idx);

% gene expression: gene lists x roi x 6 donors
load(strcat(Path,'/data/',ParcelName, '/tot_gene_expr.mat'));
tot_gene_expr_mirr             = zeros(size(tot_gene_expr));
for dn = 3:6    % 4/6 subjects have onlyl lh -> l/r mirroring
    tmp                        = tot_gene_expr(:,:,dn);
    tmp(:,NumROI/2+1:end)      = tmp(:,1:NumROI/2);
    tot_gene_expr_mirr(:,:,dn) = tmp;
end
cons_gene_expr  = mean(tot_gene_expr_mirr(cons_idx,:,:),3);

%%%%% Step #2: Correlate gene list with your maps.
load(strcat(Path,'/CDC.mat')); % replace with your map
sig_gene_tot    = cell(1,5);
X               = CDC;
Y               = cons_gene_expr';

%%% spin-permutations
% parc to vert
vert_info             = zeros(NumVert*2, 2);
vert_info(:,1)        = parc;
for nr = 1:NumROI
    idx               = find(parc == nr);
    vert_info(idx, 2) = X(nr);
end
feat_lh               = vert_info(1:NumVert,2);
feat_rh               = vert_info(1+NumVert:end,2);
% spin permutation
Xperm = spin_permutations({feat_lh,feat_rh}, {sphere_lh,sphere_rh}, NumPerm,'random_state',0);
Xrot = squeeze([Xperm{1}(:,1,:); Xperm{2}(:,1,:)]);
% vert to parc
Xrot_parc           = zeros(NumROI,NumPerm);
for nr              = 1:NumROI
    idx             = find(parc == nr);
    Xrot_parc(nr,:) = mean(Xrot(idx,:));
end

%%% corr with gene expression
r_real           = corr(X,Y);
r_perm           = zeros(NumPerm, size(Y,2));
for np           = 1:NumPerm
    r            = corr(Xrot_parc(:,np), Y);
    r_perm(np,:) = r;
end
p_perm               = zeros(1,size(Y,2));
for ng               = 1:size(Y,2)
    prctile_rank     = mean(r_real(ng) > r_perm(:,ng));
    if prctile_rank < 0.5
        p_perm(1,ng) = prctile_rank;
    else
        p_perm(1,ng) = 1 - prctile_rank;
    end
end
FDR               = mafdr(p_perm,'BHFDR', true); 
sig_idx           = find(FDR<0.05);
sig_gene_tot{1,1} = sig_idx';
sig_gene_tot{1,2} = cons_gene_list(sig_idx);
sig_gene_tot{1,3} = r_real(sig_idx);
sig_gene_tot{1,4} = FDR(sig_idx);
sig_gene_tot{1,5} = p_perm(sig_idx);

figure;
imagesc(cons_gene_expr(:,1:100)');
set(gca, 'XTick',0);set(gca, 'YTick',0);
colormap(jet);
colorbar('ticklength',[0 0]);

