for cn = 1:length(cell_names)
    disp(strcat(['cn = ',int2str(cn),' -- ',cell_names{cn}]));
    
    exp_tmp      = ['cell_gene = ',cell_names{cn},'_gene;'];
    eval(exp_tmp);
    
    ov           = numel(intersect(sig_gene_list, cell_gene));
    ov_tot(cn,1) = ov;
    ov_rat(cn,1) = (ov/length(cell_gene))*100;
    
    % statistical testing
    ov_rat_perm           = zeros(NumPerm,1);
    for np = 1:NumPerm
        permidx           = randperm(length(cell_gene_all));
        idx               = permidx(1:length(cell_gene));
        cell_gene_perm    = cell_gene_all(idx);
        
        ov                = numel(intersect(sig_gene_list, cell_gene_perm));
        ov_rat_perm(np,1) = (ov/length(cell_gene_perm))*100;
    end
    
    prctile_rank          = mean(ov_rat(cn,1) > ov_rat_perm);
    p_cor                 = 1 - prctile_rank;
    ov_p(cn,1)            = p_cor;
end

