FC_thre               = zeros(parcell_Num,parcell_Num);
for jj                = 1:parcell_Num
    FC_row            = FC(jj,:);
    p = prctile(FC_row,90);
    FCbin             = ones(size(FC_row));
    FCbin(FC_row < p) = 0;
    FC_thre(jj,:)     = FCbin;
end

% construct CGE matrix
CGE_all           = corr(cons_gene_expr,cons_gene_expr);
% construct CGE map
CC                = FC_thre.*CGE_all;
DD                = zeros(parcell_Num,20);
for kk=1:parcell_Num
    row1          = CC(kk,:);
    row1(row1==0) = [];
    DD(kk,:)      = row1;
end
CGE_map           = mean(DD,2);

